import { useState, useContext } from "react";
import { useResource } from "react-request-hook";
import { StateContext } from "../context";

export default function Todo({
  title,
  description,
  author,
  dateCreated,
  complete,
  dateCompleted,
  id,
}) {
  const { dispatch } = useContext(StateContext);
  const [isComplete, setComplete] = useState(complete);

  const [, updateTodo] = useResource((todo) => ({
    url: `/todos/${todo.id}`,
    method: "PUT",
    data: todo,
  }));

  const [, deleteTodo] = useResource((id) => ({
    url: `/todos/${id}`,
    method: "DELETE",
  }));

  const handleToggle = () => {
    const updatedTodo = {
      id,
      title,
      description,
      author,
      dateCreated,
      complete: !isComplete,
      dateCompleted: !isComplete ? new Date().toISOString() : null,
    };

    updateTodo(updatedTodo);
    setComplete(!isComplete);
    dispatch({ type: "TOGGLE_TODO", todo: updatedTodo });
  };

  const handleDelete = () => {
    deleteTodo(id);
    dispatch({ type: "DELETE_TODO", id });
  };

  return (
    <div className="card mb-2">
      <div className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="card-text">{description}</p>
        <footer className="blockquote-footer">
          Written by <cite title="Source Title">{author}</cite>
        </footer>
        <p className="card-text">
          <small className="text-muted">Created on: {new Date(dateCreated).toLocaleString()}</small>
        </p>
        <p className="card-text">
          <small className="text-muted">
            Completed on: {isComplete ? new Date(dateCompleted).toLocaleString() : "Not completed"}
          </small>
        </p>
        <div className="form-check">
          <input className="form-check-input" type="checkbox" checked={isComplete} onChange={handleToggle} />
          <label className="form-check-label" htmlFor="defaultCheck1">
            Complete
          </label>
        </div>
        <button className="btn btn-danger" onClick={handleDelete}>Delete</button>
      </div>
    </div>
  );
}
