// Handles actions related to the user
export default function userReducer(state, action) {
  switch (action.type) {
    case "LOGIN":
    case "REGISTER":
      return action.username;
    case "LOGOUT":
      return "";
    // Add a default case to ignore actions not meant for this reducer
    default:
      return state;
    }}
