// Importing necessary hooks and context for the component
import { useState, useContext, useCallback } from "react";
import { useResource } from "react-request-hook";
import { StateContext } from "../context";

// Define the component for creating new todo items
export default function CreateTodo() {
  // State hooks for form inputs
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  // Extracting state and dispatch from context
  const { state: { user }, dispatch } = useContext(StateContext);

  // Helper function to generate ISO date string
  const getISODate = useCallback(() => new Date().toISOString(), []);

  // Resource hook for creating a new todo via a POST request
  const [todos, createTodo] = useResource(({ title, description, author }) => ({
    url: "/todos",
    method: "post",
    data: { title, description, author, dateCreated: getISODate(), complete: false, dateCompleted: null },
  }));

  // Function to handle form submission
  const handleFormSubmit = (e) => {
    e.preventDefault();
    createTodo({ title, description, author: user });
  };

  // Effect hook to reset form and dispatch action on successful todo creation
  useEffect(() => {
    if (todos?.data) {
      dispatch({ type: "CREATE_TODO", ...todos.data });
      setTitle("");
      setDescription("");
    }
  }, [todos, dispatch]);

  // Form for creating a new todo item
  return (
    <form onSubmit={handleFormSubmit}>
      <div>Author: <b>{user}</b></div>
      <div>
        <label htmlFor="create-title">Title:</label>
        <input type="text" id="create-title" value={title} onChange={(e) => setTitle(e.target.value)} />
      </div>
      <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
      <input type="submit" value="Create" />
    </form>
  );
}
