import userReducer from './userReducer'; // Adjusted path
import todoReducer from './todoReducer'; // Adjusted path

// App-level reducer combining user and todo reducers
export default function appReducer(state, action) {
  return {
    user: userReducer(state.user, action),
    todos: todoReducer(state.todos, action),
  };
}
