import React, { useState, useEffect, useContext } from 'react';
import { useResource } from 'react-request-hook';
import { StateContext } from '../context';

export default function Register() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [passwordRepeat, setPasswordRepeat] = useState('');
    const { dispatch } = useContext(StateContext);
    const [user, register] = useResource((username, password) => ({
        url: "/users",
        method: "post",
        data: { email: username, password },
    }));

    useEffect(() => {
        if (user && user.data) {
            dispatch({ type: "REGISTER", username: user.data.email });
        }
    }, [user, dispatch]);

    const handleSubmit = e => {
        e.preventDefault();
        if (!/\S+@\S+\.\S+/.test(username)) {
            window.alert('Invalid email format');
            return;
        }
        if (!/(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[.]).{8,}/.test(password)) {
            window.alert('Password must be at least 8 characters, include a number and a special character (.)');
            return;
        }
        if (password !== passwordRepeat) {
            window.alert('Passwords do not match');
            return;
        }
        register(username, password);
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label htmlFor="register-username">Username:</label>
                <input type="text" value={username} onChange={e => setUsername(e.target.value)} name="register-username" id="register-username" />
                <label htmlFor="register-password">Password:</label>
                <input type="password" value={password} onChange={e => setPassword(e.target.value)} name="register-password" id="register-password" />
                <label htmlFor="register-password-repeat">Repeat password:</label>
                <input type="password" value={passwordRepeat} onChange={e => setPasswordRepeat(e.target.value)} name="register-password-repeat" id="register-password-repeat" />
                <input type="submit" value="Register" />
            </form>
        </div>
    );
}
