// ... (other imports remain unchanged)
import React, { useState, useEffect } from 'react'
import { StateContext } from '../context'
import { useContext } from 'react'
import { useResource } from 'react-request-hook'

export default function Login() {
    const [username, setUsername] = useState('')
    const {dispatch} = useContext(StateContext);
    const [loginFailed, setLoginFailed] = useState(false)
    const [error, setError] = useState('');
    const [password, setPassword] = useState('')

    const handleSubmit = e => {
      e.preventDefault(); 
      if (!/\S+@\S+\.\S+/.test(username)) {
          window.alert('Invalid email format');
      } else {
          login(username, password);
      }
  };

    function handlePassword (evt) { setPassword(evt.target.value)}

    const [user, login] = useResource((username, password) => ({
        url: "/login",
        method: "post",
        data: { email: username, password },
    }));

    useEffect(() => {
        if (user) {
            if (user?.data?.user) {
                setLoginFailed(false);
                dispatch({ type: "LOGIN", username: user.data.user.email });
            } else {
                setLoginFailed(true);
            }
        }
    }, [user, dispatch]);

    return (
        <div>
            <form onSubmit={e => { e.preventDefault(); 
                if (!error) {
                    login(username,password);
                }
            }}>
                <label htmlFor="login-username">Username:</label>
                <input type="text" value={username} onChange={handleUsername} name="login-username" id="login-username" />
                {error && <p className="error-message">{error}</p>}
                <label htmlFor="login-password">Password:</label>
                <input type="password" value={password} onChange={handlePassword} name="login-password" id="login-password" />
                <input type="submit" value="Login" disabled={username.length === 0 || error} />
            </form>
            {loginFailed && <p className="error-message">Login failed, please try again.</p>}
        </div>
    )
}
