import React, { useState, useContext } from 'react';
import { useResource } from 'react-request-hook';
import { StateContext } from './contexts';

export default function CreateTodo() {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const { state, dispatch } = useContext(StateContext);

    const [todoResult, createTodo] = useResource(({ title, description, author }) => ({
        url: '/todos',
        method: 'post',
        data: { title, description, author }
    }));

    const handleSubmit = (e) => {
        e.preventDefault();
        if (title.trim() && description.trim()) {
            createTodo({
                title,
                description,
                author: state.user.user
            });
        }
    };

    // Assuming todoResult has the properties isLoading, data, error
    // You can use these to provide feedback to the user
    const isLoading = todoResult.isLoading;
    const error = todoResult.error;

    return (
        <div>
            {isLoading && <p>Loading...</p>}
            {error && <p>Error creating todo: {error.message}</p>}

            <form onSubmit={handleSubmit}>
                <label htmlFor="title">Title:</label>
                <input
                    type="text"
                    id="title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                />

                <label htmlFor="description">Description:</label>
                <textarea
                    id="description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />

                <button type="submit" disabled={isLoading}>Add Todo</button>
            </form>
        </div>
    );
}
