import React, { useReducer } from 'react';
import './App.css';
import UserBar from './UserBar';
import CreateTodo from './CreateTodo';
import TodoList from './TodoList';
import { StateContext } from './context/contexts';
import { userReducer, userInitialState } from './reducers/userReducer';
import { todoReducer, todoInitialState } from './reducers/todoReducer';

function App() {
    const [userState, userDispatch] = useReducer(userReducer, userInitialState);
    const [todoState, todoDispatch] = useReducer(todoReducer, todoInitialState);

    const mainState = { user: userState, todo: todoState };
    const mainDispatch = action => {
        if (action.type.startsWith('USER_')) {
            userDispatch(action);
        } else if (action.type.startsWith('TODO_')) {
            todoDispatch(action);
        }
    };

    return (
        <StateContext.Provider value={{ state: mainState, dispatch: mainDispatch }}>
            <div className="App">
                <h1>ToDo App</h1>
                <UserBar />
                {userState.isLoggedIn && <CreateTodo />}
                <TodoList />
            </div>
        </StateContext.Provider>
    );
}

export default App;
