export default function userReducer(state, action) {
    switch (action.type) {
      case "LOGIN":
      case "REGISTER":
        return action.username;
      case "LOGOUT":
        return "";
      default:
        throw new Error(`Unhandled action type: ${action.type}`);
    }
  }
