import React, { useState, useContext, useEffect } from "react";
import { StateContext } from "../context";
import axios from 'axios'; // If you're not using axios, replace it with your preferred HTTP request library or the fetch API

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const { dispatch } = useContext(StateContext);

  useEffect(() => {
    // This will be used to handle the login response
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!/\S+@\S+\.\S+/.test(username)) {
      alert("Invalid email format");
      return;
    }
    try {
      // Making a POST request to the backend using axios
      const response = await axios.post('/login', {
        username, // changed from email to username
        password
      });
      if (response.status === 200) {
        dispatch({ type: "LOGIN", username: response.data.username });
        // Save the token in local storage or in context for authenticated requests
        localStorage.setItem('token', response.data.token);
      }
    } catch (error) {
      // Handle errors here, such as incorrect credentials or server issues
      if (error.response) {
        alert(error.response.data.error);
      } else {
        alert("An unexpected error occurred. Please try again.");
      }
    }
  };


  // JSX for rendering the login form
  return (
    <div className="container mt-4">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header">Login</div>
            <div className="card-body">
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <input
                    type="text"
                    className="form-control"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    name="login-username"
                    id="login-username"
                    placeholder="Email"
                  />
                </div>
                <div className="form-group">
                  <input
                    type="password"
                    className="form-control"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    name="login-password"
                    id="login-password"
                    placeholder="Password"
                  />
                </div>
                <input type="submit" className="btn btn-primary" value="Login" />
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
