import React, { useContext, useReducer, useEffect } from 'react';
import { ThemeContext, themes } from './themeContext'; 
import CreateTodo from './Todos/CreateTodo';
import TodoList from './Todos/TodoList';
import UserBar from './User/UserBar';
import appReducer from './reducer';
import { StateContext } from './context';
import { useResource } from 'react-request-hook';
import "./App.css";

function App() {
  const [state, dispatch] = useReducer(appReducer, {
    user: "",
    todos: [],
  });

  const [todosResponse, getTodos] = useResource(() => ({
    url: "/todos",
    method: "get",
  }));

  const { theme, changeTheme } = useContext(ThemeContext);
  const [darkMode, setDarkMode] = React.useState(theme === themes.dark);

  useEffect(() => {
    getTodos();
  }, [getTodos]);

  useEffect(() => {
    if (todosResponse && todosResponse.data) {
      dispatch({ type: "FETCH_TODO", todos: todosResponse.data.reverse() });
    }
  }, [todosResponse]);

  const toggleTheme = () => {
    const newTheme = darkMode ? themes.light : themes.dark;
    setDarkMode(!darkMode);
    changeTheme(newTheme);
  };

// Adjust the container's class when the user is logged in
const appClass = state.user ? 'App logged-in' : 'App';

return (
  <StateContext.Provider value={{ state, dispatch }}>
    <div className={appClass}>
      <header className="App-header">
        {/* The toggle button for dark mode is moved to the top right of the viewport */}
      </header>
      {/* Conditionally render UserBar or CreateTodo based on the user's logged-in status */}
      {!state.user ? <UserBar /> : <CreateTodo />}
      <TodoList />
      {/* This button is absolutely positioned, so it needs to be outside any .column or centered divs */}
      <button className="toggle-theme" onClick={toggleTheme}>
        {darkMode ? 'Light Mode' : 'Dark Mode'}
      </button>
    </div>
  </StateContext.Provider>
);
}

export default App;
