import React, { useState, useContext } from 'react';
import { StateContext } from './context/contexts';

export default function CreateTodo() {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const { state, dispatch } = useContext(StateContext);
    const { user } = state.user; // Correctly destructure 'user' from 'state.user'

    const handleSubmit = (event) => {
        event.preventDefault(); // Prevent form submission
        if (title.trim() && description.trim()) {
            dispatch({
                type: 'TODO_ADD_TODO',
                payload: {
                    title: title,
                    description: description,
                    author: user // Assuming 'user' contains the name of the user
                }
            });
            setTitle('');
            setDescription('');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label htmlFor="author">Author:</label>
            <input 
                type="text"
                name="author"
                id="author"
                value={user ? user.name : 'Guest'} // Corrected this line
                readOnly
            />
            <br />
            <label htmlFor="title">Title:</label>
            <input 
                type="text"
                name="title"
                id="title"
                value={title}
                onChange={e => setTitle(e.target.value)}
            />
            <br />
            <label htmlFor="description">Description:</label>
            <textarea 
                name="description"
                id="description"
                value={description}
                onChange={e => setDescription(e.target.value)}
            />
            <br />
            <input type="submit" value="Add Todo" />
        </form>
    );
}
